    public function getChildren ($categories, $parentId) {
        $return = [];

        foreach ($categories as $category) {
            if ($category->parent_category_id == $parentId) {
                // $return[$category->id] = [
                //     'id' => $category->id,
                //     'name' => $category->marker_category_name,
                //     'children' => $this->getChildren($categories, $category->id)
                // ];

                $types = [];

                if (! empty($category->toArray()['marker_types'])) {
                    foreach ($category->toArray()['marker_types'] as $type) {
                        array_push($types, $type['id']);
                    }
                }

                array_push($return, [
                    'id' => $category->id,
                    'name' => $category->marker_category_name,
                    'types' => $types,
                    'children' => $this->getChildren($categories, $category->id),
                ]);
            }
        }

        return $return;
    }

